// src/components/InicioTurmas.js
import React from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import MenuDrawer from './MenuDrawer';

export default function InicioTurmas() {
  return (
    <Box sx={{ display: 'flex', minHeight: '100vh', backgroundColor: '#F0F0F0' }}>
      {/* Menu Drawer Fixo */}
      <Box sx={{ width: 250 }}>
        <MenuDrawer buttonColor="#5922A0" />
      </Box>
      
      <Box sx={{ flexGrow: 1 }}>
        {/* Header Amarelo */}
        <Box
          sx={{
            backgroundColor: '#FFD105',
            padding: '16px',
          }}
        >
          <Typography
            variant="h4"
            sx={{ color: '#5922A0', textAlign: 'left', fontWeight: 'bold' }}
          >
            Kingstown - Sala de aula
          </Typography>
        </Box>

        {/* Conteúdo Principal */}
        <Box sx={{ padding: '24px' }}>
          <Grid container spacing={3}>
            {/* Cartões das Turmas */}
            {Array.from({ length: 6 }).map((_, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Paper
                  elevation={3}
                  sx={{
                    height: 150,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'flex-end',
                    backgroundColor: '#E0E0E0',
                    overflow: 'hidden',
                  }}
                >
                  <Box
                    sx={{
                      backgroundColor: '#5922A0',
                      color: '#FFF',
                      textAlign: 'center',
                      padding: '8px',
                    }}
                  >
                    Kingstown College
                  </Box>
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
}
