// src/components/InicioTurmas/InicioTurmas.js
import React from 'react';
import { Box, CssBaseline, Toolbar, Typography, Grid, Card, CardContent } from '@mui/material';
import { Routes, Route } from 'react-router-dom';
import MenuDrawer from '../MenuDrawer';
import TurmasList from '../ProfessorDashboard/TurmasList';
import AlunosList from '../ProfessorDashboard/AlunosList';
import Configuracoes from '../ProfessorDashboard/Configuracoes';
import CriarAlunos from '../ProfessorDashboard/CriarAlunos'; 
import CriarSalas from '../ProfessorDashboard/CriarSalas'; 

const salas = [
  { id: 1, nome: 'Sala de Matemática' },
  { id: 2, nome: 'Sala de Ciências' },
  { id: 3, nome: 'Sala de História' },
  { id: 4, nome: 'Sala de Geografia' },
];

export default function InicioTurmas() {
  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <MenuDrawer />

      <Box component="main" sx={{ flexGrow: 1, p: 3, marginLeft: '240px' }}>
        <Toolbar />
        <Typography variant="h4" sx={{ color: '#5922A0', marginBottom: 2 }}>
          Salas Criadas
        </Typography>
        <Grid container spacing={2}>
          {salas.map((sala) => (
            <Grid item xs={12} sm={6} md={4} key={sala.id}>
              <Card sx={{ backgroundColor: '#FFD105', color: '#5922A0' }}>
                <CardContent>
                  <Typography variant="h5">{sala.nome}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        <Routes>
          <Route path="/" element={<TurmasList />} />
          <Route path="/turmas" element={<TurmasList />} />
          <Route path="/alunos" element={<AlunosList />} />
          <Route path="/configuracoes" element={<Configuracoes />} />
          <Route path="/salas" element={<CriarSalas />} />
          <Route path="/criar-alunos" element={<CriarAlunos />} /> 
        </Routes>
      </Box>
    </Box>
  );
}
