// src/components/ProfessorDashboard/TurmasList.js
import React, { useState } from 'react';
import { Box, Typography, Button, Card, CardContent, CardActions, Grid } from '@mui/material';
import NovaTurmaModal from './NovaTurmaModal';
import EditarTurmaModal from './EditarTurmaModal';

const TurmasList = () => {
  const [openNovaTurmaModal, setOpenNovaTurmaModal] = useState(false);
  const [openEditarTurmaModal, setOpenEditarTurmaModal] = useState(false);
  const [turmaSelecionada, setTurmaSelecionada] = useState(null);

  const turmas = [
    { id: 1, nome: 'Turma A', alunos: 25 },
    { id: 2, nome: 'Turma B', alunos: 30 },
    { id: 3, nome: 'Turma C', alunos: 20 },
  ];

  const handleOpenNovaTurmaModal = () => setOpenNovaTurmaModal(true);
  const handleCloseNovaTurmaModal = () => setOpenNovaTurmaModal(false);
  
  const handleOpenEditarTurmaModal = (turma) => {
    setTurmaSelecionada(turma);
    setOpenEditarTurmaModal(true);
  };

  const handleCloseEditarTurmaModal = () => {
    setOpenEditarTurmaModal(false);
    setTurmaSelecionada(null);
  };

  const handleSaveTurma = (turmaAtualizada) => {
    console.log('Turma atualizada:', turmaAtualizada);
    handleCloseEditarTurmaModal(); // Fecha o modal após salvar
  };

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 3, color: '#5922A0' }}>Minhas Turmas</Typography>
      <Grid container spacing={2}>
        {turmas.map((turma) => (
          <Grid item xs={12} sm={6} md={4} key={turma.id}>
            <Card sx={{ backgroundColor: '#FFD105' }}>
              <CardContent>
                <Typography variant="h6" sx={{ color: '#5922A0' }}>{turma.nome}</Typography>
                <Typography variant="body2" sx={{ color: '#5922A0' }}>Alunos: {turma.alunos}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small" sx={{ color: '#5922A0' }} onClick={() => handleOpenEditarTurmaModal(turma)}>Editar</Button>
                <Button size="small" sx={{ color: '#5922A0' }}>Ver Detalhes</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Button
        variant="contained"
        sx={{
          mt: 3,
          backgroundColor: '#5922A0',
          color: '#FFD105',
          '&:hover': { backgroundColor: '#3a157a' }
        }}
        onClick={handleOpenNovaTurmaModal}
      >
        Criar Nova Turma
      </Button>
      <NovaTurmaModal open={openNovaTurmaModal} handleClose={handleCloseNovaTurmaModal} />
      <EditarTurmaModal 
        open={openEditarTurmaModal} 
        handleClose={handleCloseEditarTurmaModal} 
        turma={turmaSelecionada} 
        onSave={handleSaveTurma} 
      />
    </Box>
  );
};

export default TurmasList;
