// src/components/MenuDrawer.js
import React from 'react';
import {
  Drawer,
  IconButton,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Box,
  Typography,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const MenuDrawer = ({ buttonColor }) => {
  const [open, setOpen] = React.useState(false);

  const toggleDrawer = (open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setOpen(open);
  };

  const list = () => (
    <Box
      sx={{
        width: 250,
        backgroundColor: '#f5f5f5', // Cor de fundo do Drawer
        height: '100%', // Altura total do Drawer
      }}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <Typography
        variant="h6"
        sx={{ padding: 2, textAlign: 'center', color: '#5922A0' }} // Estilo do título
      >
        Kingstown - Sala de Aula
      </Typography>
      <Divider />
      <List>
        <ListItem button onClick={toggleDrawer(false)}>
          <ListItemText primary="Opção 1" />
        </ListItem>
        <ListItem button onClick={toggleDrawer(false)}>
          <ListItemText primary="Opção 2" />
        </ListItem>
      </List>
    </Box>
  );

  return (
    <>
      <IconButton
        onClick={toggleDrawer(true)}
        sx={{
          color: buttonColor, // Define a cor do botão
          '&:hover': { color: buttonColor, opacity: 0.8 }, // Efeito de hover
        }}
      >
        <MenuIcon />
      </IconButton>
      <Drawer anchor="left" open={open} onClose={toggleDrawer(false)}>
        {list()}
      </Drawer>
    </>
  );
};

export default MenuDrawer;
