import React from 'react';
import { Box, Divider, List, ListItem, ListItemIcon, ListItemText, Drawer, IconButton } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu'; // Ícone de menu
import { useState } from 'react';

const MenuDrawer = () => {
  const [open, setOpen] = useState(false);

  const toggleDrawer = (open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setOpen(open);
  };

  const drawerContent = (
    <Box
      sx={{ width: 240 }}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        <ListItem button>
          <ListItemText primary="Criar Turma" />
        </ListItem>
        <Divider />
        <ListItem button>
          <ListItemText primary="Sobre" />
        </ListItem>
        <ListItem button>
          <ListItemText primary="Configurações" />
        </ListItem>
      </List>
    </Box>
  );

  return (
    <>
      <IconButton onClick={toggleDrawer(true)} sx={{ color: '#5922A0' }}>
        <MenuIcon />
      </IconButton>
      <Drawer anchor="left" open={open} onClose={toggleDrawer(false)}>
        {drawerContent}
      </Drawer>
    </>
  );
};
