import * as React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import HomeIcon from '@mui/icons-material/Home';
import ClassIcon from '@mui/icons-material/Class';
import PersonAddIcon from '@mui/icons-material/PersonAdd'; // Ícone para Criar Alunos
import MeetingRoomIcon from '@mui/icons-material/MeetingRoom'; // Ícone para Criar Salas
import SettingsIcon from '@mui/icons-material/Settings';
import { useNavigate } from 'react-router-dom'; // Importar o hook useNavigate

const drawerWidth = 240;

export default function ClippedDrawer() {
  const navigate = useNavigate(); // Hook para navegação

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{
          zIndex: (theme) => theme.zIndex.drawer + 1,
          backgroundColor: '#FFD105' // Cor de fundo amarela
        }}
      >
        <Toolbar>
          <Typography
            variant="h6"
            noWrap
            component="div"
            sx={{ color: '#5922A0' }} // Cor roxa para o texto
          >
            Kingstown - Sala de Aula
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: 'border-box',
            backgroundColor: '#FFD105' // Cor de fundo amarela
          },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: 'auto' }}>
          <List>
            {[ 
              { text: 'Início', icon: <HomeIcon />, path: '/' },
              { text: 'Minhas Turmas', icon: <ClassIcon />, path: '/minhas-turmas' },
              { text: 'Criar Alunos', icon: <PersonAddIcon />, path: '/criar-alunos' }, // Novo item: Criar Alunos
              { text: 'Criar Salas', icon: <MeetingRoomIcon />, path: '/criar-salas' }, // Novo item: Criar Salas
            ].map((item) => (
              <ListItem button key={item.text} onClick={() => navigate(item.path)}>
                <ListItemIcon sx={{ color: '#5922A0' }}>
                  {item.icon}
                </ListItemIcon>
                <ListItemText primary={item.text} sx={{ color: '#5922A0' }} /> {/* Cor roxa para os textos */}
              </ListItem>
            ))}
          </List>
          <Divider />
          <List>
            <ListItem button>
              <ListItemIcon sx={{ color: '#5922A0' }}>
                <SettingsIcon />
              </ListItemIcon>
              <ListItemText primary="Configurações" sx={{ color: '#5922A0' }} /> {/* Cor roxa para o texto */}
            </ListItem>
          </List>
        </Box>
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
      </Box>
    </Box>
  );
}
