import React from 'react';
import { Container, Typography, Button, TextField, Box } from '@mui/material';

function CriarTurma() {
  return (
    <Container 
      maxWidth="md" 
      sx={{ 
        bgcolor: '#FFEB3B', // Amarelo para o fundo
        color: '#673AB7', // Roxo para o texto
        borderRadius: 2,
        p: 4,
        mt: 4,
      }}
    >
      <Typography variant="h4" gutterBottom>
        Criar Nova Turma
      </Typography>
      <Box 
        component="form" 
        sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}
      >
        <TextField label="Nome da Turma" variant="outlined" fullWidth />
        <TextField label="Descrição" variant="outlined" fullWidth multiline rows={3} />
        <Button variant="contained" color="primary" sx={{ bgcolor: '#673AB7' }}>
          Criar Turma
        </Button>
      </Box>
    </Container>
  );
}

export default CriarTurma;
