import * as React from 'react';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import PeopleIcon from '@mui/icons-material/People';
import ClassIcon from '@mui/icons-material/Class';
import { useTheme } from '@mui/material/styles';

const ProfessorDashboard = () => {
  const theme = useTheme();

  // Dados fictícios para o resumo. Vamos substituir por dados reais depois.
  const totalAlunos = 50;
  const totalSalas = 10;

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3, color: '#5922A0' }}>
        Dashboard do Professor
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} md={6} lg={4}>
          <Card sx={{ backgroundColor: '#FFD105' }}>
            <CardContent>
              <Grid container spacing={2} alignItems="center">
                <Grid item>
                  <PeopleIcon sx={{ fontSize: 40, color: '#5922A0' }} />
                </Grid>
                <Grid item>
                  <Typography variant="h6">Total de Alunos</Typography>
                  <Typography variant="h4">{totalAlunos}</Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={6} lg={4}>
          <Card sx={{ backgroundColor: '#FFD105' }}>
            <CardContent>
              <Grid container spacing={2} alignItems="center">
                <Grid item>
                  <ClassIcon sx={{ fontSize: 40, color: '#5922A0' }} />
                </Grid>
                <Grid item>
                  <Typography variant="h6">Total de Salas</Typography>
                  <Typography variant="h4">{totalSalas}</Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
        {/* Podemos adicionar mais cartões aqui, como gráficos ou outras métricas */}
      </Grid>
    </Box>
  );
};

export default ProfessorDashboard;
