// src/components/ProfessorDashboard/EditarTurmaModal.js
import React, { useState, useEffect } from 'react';
import { Box, Button, Modal, TextField, Typography } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: '#FFF',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

const EditarTurmaModal = ({ open, handleClose, turma, onSave }) => {
  const [nomeTurma, setNomeTurma] = useState('');
  const [quantidadeAlunos, setQuantidadeAlunos] = useState('');

  useEffect(() => {
    if (turma) {
      setNomeTurma(turma.nome);
      setQuantidadeAlunos(turma.alunos);
    }
  }, [turma]);

  const handleSubmit = () => {
    onSave({ ...turma, nome: nomeTurma, alunos: quantidadeAlunos }); // Envia as informações editadas
    handleClose();
  };

  return (
    <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box sx={style}>
        <Typography id="modal-modal-title" variant="h6" component="h2" sx={{ color: '#5922A0' }}>
          Editar Turma
        </Typography>
        <TextField
          fullWidth
          label="Nome da Turma"
          variant="outlined"
          value={nomeTurma}
          onChange={(e) => setNomeTurma(e.target.value)}
          sx={{ mt: 2 }}
        />
        <TextField
          fullWidth
          label="Quantidade de Alunos"
          type="number"
          variant="outlined"
          value={quantidadeAlunos}
          onChange={(e) => setQuantidadeAlunos(e.target.value)}
          sx={{ mt: 2 }}
        />
        <Button
          variant="contained"
          sx={{
            mt: 3,
            backgroundColor: '#5922A0',
            color: '#FFD105',
            '&:hover': { backgroundColor: '#3a157a' }
          }}
          onClick={handleSubmit}
        >
          Salvar Alterações
        </Button>
      </Box>
    </Modal>
  );
};

export default EditarTurmaModal;
