// src/components/InicioTurmas/InicioTurmas.js
import React from 'react';
import {
  Box,
  CssBaseline,
  Toolbar,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Avatar,
} from '@mui/material';
import MenuDrawer from '../MenuDrawer';

// Dados atualizados para aulas de idiomas
const salas = [
  { id: 1, nome: 'Inglês Avançado', descricao: 'Aperfeiçoamento em gramática e conversação' },
  { id: 2, nome: 'Espanhol Básico', descricao: 'Introdução à língua e vocabulário' },
  { id: 3, nome: 'Francês Intermediário', descricao: 'Prática de leitura e escrita' },
  { id: 4, nome: 'Alemão para Iniciantes', descricao: 'Fundamentos da língua e cultura' },
];

export default function InicioTurmas() {
  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <MenuDrawer />

      <Box component="main" sx={{ flexGrow: 1, p: 3, marginLeft: '240px' }}>
        <Toolbar />
        <Typography variant="h4" sx={{ color: '#5922A0', marginBottom: 2 }}>
          Minhas Turmas
        </Typography>
        <Grid container spacing={2}>
          {salas.map((sala) => (
            <Grid item xs={12} sm={6} md={4} key={sala.id}>
              <Card sx={{ backgroundColor: '#FFD105', color: '#5922A0', borderRadius: 2, boxShadow: 3 }}>
                <CardContent>
                  <Avatar sx={{ backgroundColor: '#5922A0', mb: 2 }}>
                    <Typography variant="h6">{sala.nome.charAt(0)}</Typography>
                  </Avatar>
                  <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
                    {sala.nome}
                  </Typography>
                  <Typography variant="body2" sx={{ marginTop: 1 }}>
                    {sala.descricao}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" variant="contained" sx={{ backgroundColor: '#5922A0', color: '#fff' }}>
                    Ver Detalhes
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
}
