// src/pages/InicioTurmas.js
import React from 'react';
import { Box, Typography, Card, CardContent, Grid } from '@mui/material';

const aulas = [
  { title: 'Aula de Inglês', description: 'Aperfeiçoamento de Conversação', id: 1 },
  { title: 'Aula de Espanhol', description: 'Gramática e Vocabulário', id: 2 },
  { title: 'Aula de Francês', description: 'Conversação Básica', id: 3 },
];

const InicioTurmas = () => {
  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', paddingLeft: '16px' }}>
      <Typography variant="h4" gutterBottom>
        Minhas Turmas
      </Typography>
      <Grid container spacing={2} sx={{ marginLeft: '-16px' }}> {/* Margem negativa */}
        {aulas.map((aula) => (
          <Grid item key={aula.id} xs={12} sm={6} md={4}>
            <Card sx={{ width: '100%', height: '200px', display: 'flex', flexDirection: 'column', justifyContent: 'center', marginBottom: '16px' }}>
              <CardContent>
                <Typography variant="h5">{aula.title}</Typography>
                <Typography variant="body2">{aula.description}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default InicioTurmas;
