// src/components/ProfessorDashboard/NovaTurmaModal.js
import React, { useState } from 'react';
import { Box, Button, Modal, TextField, Typography } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: '#FFF',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

const NovaTurmaModal = ({ open, handleClose }) => {
  const [nomeTurma, setNomeTurma] = useState('');
  const [quantidadeAlunos, setQuantidadeAlunos] = useState('');

  const handleSubmit = () => {
    // Lógica para salvar a nova turma (ex: chamada para API ou atualização de estado)
    console.log('Nova turma criada:', nomeTurma, quantidadeAlunos);
    handleClose(); // Fecha o modal após o submit
  };

  return (
    <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box sx={style}>
        <Typography id="modal-modal-title" variant="h6" component="h2" sx={{ color: '#5922A0' }}>
          Criar Nova Turma
        </Typography>
        <TextField
          fullWidth
          label="Nome da Turma"
          variant="outlined"
          value={nomeTurma}
          onChange={(e) => setNomeTurma(e.target.value)}
          sx={{ mt: 2 }}
        />
        <TextField
          fullWidth
          label="Quantidade de Alunos"
          type="number"
          variant="outlined"
          value={quantidadeAlunos}
          onChange={(e) => setQuantidadeAlunos(e.target.value)}
          sx={{ mt: 2 }}
        />
        <Button
          variant="contained"
          sx={{
            mt: 3,
            backgroundColor: '#5922A0',
            color: '#FFD105',
            '&:hover': { backgroundColor: '#3a157a' }
          }}
          onClick={handleSubmit}
        >
          Criar Turma
        </Button>
      </Box>
    </Modal>
  );
};

export default NovaTurmaModal;
