import * as React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import MenuIcon from '@mui/icons-material/Menu';

const drawerWidth = 240;

export default function ProfessorDashboard() {
  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{
          zIndex: (theme) => theme.zIndex.drawer + 1,
          backgroundColor: '#FFD105',
        }}
      >
        <Toolbar>
          <Typography variant="h6" noWrap component="div" sx={{ color: '#5922A0' }}>
            Kingstown - Sala de Aula
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: 'border-box',
            backgroundColor: '#FFD105',
          },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: 'auto' }}>
          {/* Aqui virão os itens do Menu Drawer */}
        </Box>
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Toolbar />
        <Typography variant="h4" gutterBottom>
          Painel do Professor
        </Typography>
        {/* Seção de Criação de Alunos */}
        <Typography variant="h6">Adicionar Novo Aluno</Typography>
        <TextField label="Nome do Aluno" variant="outlined" sx={{ mb: 2 }} fullWidth />
        <TextField label="Email do Aluno" variant="outlined" sx={{ mb: 2 }} fullWidth />
        <Button variant="contained" color="primary">
          Adicionar Aluno
        </Button>

        {/* Seção de Gerenciamento de Turmas */}
        <Typography variant="h6" sx={{ mt: 4 }}>
          Gerenciar Turmas
        </Typography>
        {/* Aqui você pode adicionar uma lista de turmas ou um botão para criar novas turmas */}
      </Box>
    </Box>
  );
}
