import React, { useState } from 'react';
import { Box, Drawer, Button, List, ListItem, ListItemText, IconButton } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

function MenuDrawer() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDrawer = (open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setIsOpen(open);
  };

  const menuItems = [
    { text: 'Criar Turma', path: '/criar-turma' },
    { text: 'Configurações', path: '/settings' },
    { text: 'Sair', path: '/' }
  ];

  const DrawerList = () => (
    <Box
      sx={{ width: 250, bgcolor: '#FFEB3B', height: '100%' }}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        {menuItems.map((item, index) => (
          <ListItem button key={index}>
            <ListItemText primary={item.text} sx={{ color: '#673AB7' }} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <div>
      <IconButton
        onClick={toggleDrawer(true)}
        sx={{ color: '#673AB7', position: 'fixed', top: 16, left: 16 }}
      >
        <MenuIcon />
      </IconButton>
      <Drawer
        anchor="left"
        open={isOpen}
        onClose={toggleDrawer(false)}
      >
        {DrawerList()}
      </Drawer>
    </div>
  );
}

export default MenuDrawer;
