// src/components/InicioTurmas.js
import React from 'react';
import { Box, Typography, Grid, Card, CardContent } from '@mui/material';
import MenuDrawer from './MenuDrawer'; // Importar o MenuDrawer corretamente

export default function InicioTurmas() {
  return (
    <Box sx={{ display: 'flex' }}>
      <MenuDrawer buttonColor="#5922A0" /> {/* Passando a cor roxa para o MenuDrawer */}
      <Box
        sx={{
          flexGrow: 1,
          padding: 3,
          backgroundColor: '#FFD105', // Cor de fundo amarelo
        }}
      >
        <Typography
          variant="h4"
          sx={{ textAlign: 'center', marginBottom: 3, color: '#5922A0' }} // Estilo do título na parte superior
        >
          Kingstown - Sala de aula
        </Typography>
        <Grid container spacing={2}>
          {[...Array(6)].map((_, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card
                sx={{
                  backgroundColor: '#E0E0E0',
                  borderRadius: 2,
                  boxShadow: 3,
                }}
              >
                <CardContent>
                  <Box
                    sx={{
                      height: 150,
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'center',
                      backgroundColor: '#5922A0',
                      color: '#FFFFFF',
                      borderRadius: '0 0 8px 8px',
                    }}
                  >
                    Kingstown College
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
}
