// src/pages/InicioTurmas.js
import React from 'react';
import { Box, Typography, Card, CardContent, Grid } from '@mui/material';

const aulas = [
  { title: 'Aula de Inglês', description: 'Aperfeiçoamento de Conversação', id: 1 },
  { title: 'Aula de Espanhol', description: 'Gramática e Vocabulário', id: 2 },
  { title: 'Aula de Francês', description: 'Conversação Básica', id: 3 },
];

const InicioTurmas = () => {
  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '16px', backgroundColor: '#FFD105' }}>
      <Typography variant="h4" gutterBottom sx={{ color: '#5922A0', marginBottom: '24px', textAlign: 'center' }}>
        Minhas Turmas
      </Typography>
      <Grid container spacing={2} justifyContent="center">
        {aulas.map((aula) => (
          <Grid item key={aula.id} xs={12} sm={6} md={4}>
            <Card
              sx={{
                width: '100%',
                height: '200px',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center', // Centraliza o conteúdo do card
                marginBottom: '16px',
                backgroundColor: '#fff', // Cor de fundo do card
                boxShadow: 3, // Sombra do card
                borderRadius: '8px', // Arredondamento dos cantos do card
              }}
            >
              <CardContent>
                <Typography variant="h5" sx={{ color: '#5922A0', textAlign: 'center' }}>{aula.title}</Typography>
                <Typography variant="body2" sx={{ textAlign: 'center' }}>{aula.description}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default InicioTurmas;
