// src/components/CriarTurma/index.js

import React, { useState } from 'react';
import { Button, TextField, Typography, Container } from '@mui/material';
import './CriarTurma.css'; // Importando o CSS separado

const CriarTurma = () => {
    const [turma, setTurma] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        // L처gica para criar turma
    };

    return (
        <Container>
            <Typography variant="h4">Criar Turma</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Nome da Turma"
                    variant="outlined"
                    value={turma}
                    onChange={(e) => setTurma(e.target.value)}
                    fullWidth
                    required
                />
                <Button
                    type="submit"
                    variant="contained"
                    className="submit-button"  // Certifique-se de que as aspas est찾o corretas
                >
                    Criar Turma
                </Button>
            </form>
        </Container>
    );
};

export default CriarTurma;
