import React from 'react';
import { Box } from '@mui/material';
import logo from '../../img/logo_roxo(2)_png.png'; // Verifique se este caminho está correto

const InicioTurmas = () => {
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center', // Centraliza verticalmente
        padding: '16px',
        marginTop: '64px',
        minHeight: 'calc(100vh - 64px)', // Altura mínima sem rolagem
        position: 'relative', // Para a imagem de fundo
      }}
    >
      {/* Logo da escola */}
      <img
        src={logo}
        alt="Logo da Escola"
        style={{
          width: '900px', // Aumenta o tamanho da logo
          opacity: 0.3, // Transparência
          position: 'absolute', // Mantém a posição absoluta
          top: '50%', // Centraliza verticalmente
          left: '50%', // Centraliza horizontalmente
          transform: 'translate(-50%, -50%)', // Centraliza a imagem
        }}
      />
      {/* Aqui você pode adicionar mais conteúdo no futuro */}
    </Box>
  );
};

export default InicioTurmas;
